import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../config/axios';
import toast from 'react-hot-toast';
import { 
  Users, 
  Plus, 
  Search, 
  Eye, 
  Edit, 
  Trash2, 
  Mail,
  Phone,
  Building,
  MoreHorizontal,
  Filter,
  SortAsc,
  SortDesc
} from 'lucide-react';
import Modal from '../components/ui/Modal';

const Clients = () => {
  // Sample data for demonstration
  const sampleClients = [
    {
      id: 1,
      name: 'Lisa Rodriguez',
      company: 'Verde Sustainability Consulting',
      email: 'lisa.rodriguez@verdesustain.com',
      phone: '+1-555-4567',
      status: 'active',
      activeProjects: 3,
      totalRevenue: 15000,
      lastContact: '2024-01-10',
      created_at: '2023-09-19'
    },
    {
      id: 2,
      name: 'Michael Chen',
      company: 'TechFlow Innovations',
      email: 'm.chen@techflow.io',
      phone: '+1-555-7890',
      status: 'active',
      activeProjects: 2,
      totalRevenue: 12000,
      lastContact: '2024-01-08',
      created_at: '2023-09-19'
    },
    {
      id: 3,
      name: 'Sarah Johnson',
      company: 'Johnson Marketing Solutions',
      email: 'sarah@johnsonmarketing.com',
      phone: '+1-555-0123',
      status: 'pending',
      activeProjects: 1,
      totalRevenue: 8000,
      lastContact: '2024-01-05',
      created_at: '2023-09-19'
    },
    {
      id: 4,
      name: 'Ann Smith',
      company: 'Acme Corporation',
      email: 'ann.smith@acmecorp.com',
      phone: '+1-555-123-4567',
      status: 'active',
      activeProjects: 4,
      totalRevenue: 25000,
      lastContact: '2024-01-12',
      created_at: '2023-09-19'
    }
  ];

  const [clients, setClients] = useState(sampleClients);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  useEffect(() => {
    setLoading(false);
  }, []);

  const handleDelete = async (client) => {
    if (window.confirm(`Are you sure you want to delete ${client.name}?`)) {
      try {
        await api.delete(`/clients/${client.id}`);
        setClients(clients.filter(c => c.id !== client.id));
        toast.success('Client deleted successfully');
      } catch (error) {
        console.error('Error deleting client:', error);
        toast.error('Failed to delete client');
      }
    }
  };

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const filteredClients = clients
    .filter(client => {
      const matchesSearch = 
        client.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        client.company?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        client.email?.toLowerCase().includes(searchTerm.toLowerCase());
      return matchesSearch;
    })
    .sort((a, b) => {
      let aValue = a[sortBy];
      let bValue = b[sortBy];
      
      if (sortBy === 'created_at' || sortBy === 'lastContact') {
        aValue = new Date(aValue);
        bValue = new Date(bValue);
      }
      
      if (sortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

  const getStatusBadge = (status) => {
    const styles = {
      active: 'bg-success-100 text-success-700',
      pending: 'bg-warning-100 text-warning-700',
      inactive: 'bg-gray-100 text-gray-700'
    };
    
    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${styles[status]}`}>
        {status}
      </span>
    );
  };

  const getSortIcon = (column) => {
    if (sortBy !== column) {
      return <SortAsc className="w-4 h-4 text-gray-400" />;
    }
    return sortOrder === 'asc' ? 
      <SortAsc className="w-4 h-4 text-gray-600" /> : 
      <SortDesc className="w-4 h-4 text-gray-600" />;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto space-y-6">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-1/4 mb-2"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2"></div>
          </div>
          <div className="card">
            <div className="card-body">
              <div className="animate-pulse space-y-4">
                {[...Array(5)].map((_, i) => (
                  <div key={i} className="h-16 bg-gray-200 rounded"></div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <div className="max-w-7xl mx-auto p-3 space-y-3">
        {/* Page Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold space-text-primary">Client Galaxy</h1>
            <p className="space-text-secondary text-sm">Navigate your network of professional relationships</p>
          </div>
          <button
            onClick={() => setShowAddModal(true)}
            className="space-btn flex items-center gap-2 px-6 py-3"
          >
            <Plus className="w-4 h-4" />
            Add New Client
          </button>
        </div>

        {/* Search and Filters */}
        <div className="flex items-center justify-between">
          <div className="relative flex-1 max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search clients..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="space-input pl-10"
            />
          </div>
          <div className="flex items-center gap-3">
            <button className="space-btn-secondary flex items-center gap-2 px-4 py-2">
              <Filter className="w-4 h-4" />
              Filters
            </button>
            <div className="text-sm space-text-secondary">
              {filteredClients.length} of {clients.length} clients
            </div>
          </div>
        </div>

        {/* Clients Table */}
        <div className="space-card">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-white/10">
                  <th
                    className="cursor-pointer hover:bg-white/5 transition-colors py-4 px-6 text-left"
                    onClick={() => handleSort('name')}
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-sm font-semibold space-text-primary">Client</span>
                      {getSortIcon('name')}
                    </div>
                  </th>
                  <th
                    className="cursor-pointer hover:bg-white/5 transition-colors py-4 px-6 text-left"
                    onClick={() => handleSort('company')}
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-sm font-semibold space-text-primary">Company</span>
                      {getSortIcon('company')}
                    </div>
                  </th>
                  <th className="text-sm font-semibold space-text-primary py-4 px-6 text-left">Contact</th>
                  <th className="text-sm font-semibold space-text-primary py-4 px-6 text-left">Status</th>
                  <th
                    className="cursor-pointer hover:bg-white/5 transition-colors py-4 px-6 text-left"
                    onClick={() => handleSort('activeProjects')}
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-sm font-semibold space-text-primary">Projects</span>
                      {getSortIcon('activeProjects')}
                    </div>
                  </th>
                  <th
                    className="cursor-pointer hover:bg-white/5 transition-colors py-4 px-6 text-left"
                    onClick={() => handleSort('totalRevenue')}
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-sm font-semibold space-text-primary">Revenue</span>
                      {getSortIcon('totalRevenue')}
                    </div>
                  </th>
                  <th
                    className="cursor-pointer hover:bg-white/5 transition-colors py-4 px-6 text-left"
                    onClick={() => handleSort('lastContact')}
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-sm font-semibold space-text-primary">Last Contact</span>
                      {getSortIcon('lastContact')}
                    </div>
                  </th>
                  <th className="text-right text-sm font-semibold space-text-primary py-4 px-6">Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredClients.map((client) => (
                  <tr key={client.id} className="hover:bg-white/5 transition-colors border-b border-white/5">
                    <td className="py-4 px-6">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                          <span className="text-sm font-semibold text-white">
                            {client.name?.charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <div className="font-semibold space-text-primary">{client.name}</div>
                        </div>
                      </div>
                    </td>
                    <td className="py-4 px-6">
                      <div className="flex items-center gap-2">
                        <Building className="w-4 h-4 text-gray-400" />
                        <span className="text-sm space-text-secondary">{client.company}</span>
                      </div>
                    </td>
                    <td className="py-4 px-6">
                      <div className="space-y-1">
                        <div className="flex items-center gap-2 text-sm space-text-secondary">
                          <Mail className="w-3 h-3 text-gray-400" />
                          <span className="truncate max-w-32">{client.email}</span>
                        </div>
                        <div className="flex items-center gap-2 text-sm space-text-muted">
                          <Phone className="w-3 h-3 text-gray-400" />
                          <span>{client.phone}</span>
                        </div>
                      </div>
                    </td>
                    <td className="py-4 px-6">
                      {getStatusBadge(client.status)}
                    </td>
                    <td className="py-4 px-6">
                      <div className="text-center">
                        <span className="text-lg font-bold space-text-primary">{client.activeProjects}</span>
                      </div>
                    </td>
                    <td className="py-4 px-6">
                      <div className="text-right">
                        <span className="font-bold space-text-primary text-lg">
                          ${client.totalRevenue.toLocaleString()}
                        </span>
                      </div>
                    </td>
                    <td className="py-4 px-6">
                      <span className="text-sm space-text-muted">
                        {new Date(client.lastContact).toLocaleDateString()}
                      </span>
                    </td>
                    <td className="py-4 px-6">
                      <div className="flex items-center justify-end gap-2">
                        <button
                          onClick={() => setSelectedClient(client)}
                          className="p-2 text-gray-400 hover:text-blue-400 hover:bg-blue-500/20 rounded-lg transition-colors"
                          title="View"
                        >
                          <Eye className="w-4 h-4" />
                        </button>
                        <Link
                          to={`/clients/${client.id}/edit`}
                          className="p-2 text-gray-400 hover:text-green-400 hover:bg-green-500/20 rounded-lg transition-colors"
                          title="Edit"
                        >
                          <Edit className="w-4 h-4" />
                        </Link>
                        <button
                          onClick={() => handleDelete(client)}
                          className="p-2 text-gray-400 hover:text-red-400 hover:bg-red-500/20 rounded-lg transition-colors"
                          title="Delete"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Empty State */}
          {filteredClients.length === 0 && (
            <div className="text-center py-12">
              <Users className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No clients found</h3>
              <p className="text-gray-600 mb-6">
                {searchTerm 
                  ? 'Try adjusting your search criteria.' 
                  : 'Get started by adding your first client.'
                }
              </p>
              {!searchTerm && (
                <button
                  onClick={() => setShowAddModal(true)}
                  className="btn btn-primary"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Add Client
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Add Client Modal */}
      <Modal
        isOpen={showAddModal}
        onClose={() => setShowAddModal(false)}
        title="Add New Client"
        size="md"
      >
        <form className="space-y-4">
          <div>
            <label className="form-label">Full Name</label>
            <input
              type="text"
              className="form-input"
              placeholder="Enter client's full name"
            />
          </div>
          <div>
            <label className="form-label">Company</label>
            <input
              type="text"
              className="form-input"
              placeholder="Enter company name"
            />
          </div>
          <div>
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-input"
              placeholder="Enter email address"
            />
          </div>
          <div>
            <label className="form-label">Phone</label>
            <input
              type="tel"
              className="form-input"
              placeholder="Enter phone number"
            />
          </div>
          <div className="flex items-center justify-end gap-3 pt-4">
            <button
              type="button"
              onClick={() => setShowAddModal(false)}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
            >
              Add Client
            </button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default Clients;
