import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { 
  Users, 
  FolderOpen, 
  FileText, 
  DollarSign, 
  TrendingUp, 
  TrendingDown,
  Plus,
  ArrowRight,
  Calendar,
  Clock,
  Target,
  Award,
  Activity,
  BarChart3,
  PieChart
} from 'lucide-react';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalClients: 0,
    totalProjects: 0,
    totalInvoices: 0,
    totalRevenue: 0,
    activeProjects: 0,
    completedProjects: 0,
    pendingInvoices: 0,
    paidInvoices: 0
  });
  const [recentProjects, setRecentProjects] = useState([]);
  const [recentInvoices, setRecentInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [animatedStats, setAnimatedStats] = useState({});

  useEffect(() => {
    fetchDashboardData();
  }, []);

  useEffect(() => {
    // Animate stats on load
    const timer = setTimeout(() => {
      setAnimatedStats(stats);
    }, 500);
    return () => clearTimeout(timer);
  }, [stats]);

  const fetchDashboardData = async () => {
    try {
      const [clientsRes, projectsRes, invoicesRes] = await Promise.all([
        axios.get('/api/clients'),
        axios.get('/api/projects'),
        axios.get('/api/invoices')
      ]);

      const clients = clientsRes.data.clients || [];
      const projects = projectsRes.data.projects || [];
      const invoices = invoicesRes.data.invoices || [];

      const totalRevenue = invoices.reduce((sum, invoice) => {
        return invoice.status === 'Paid' ? sum + parseFloat(invoice.amount) : sum;
      }, 0);

      const activeProjects = projects.filter(p => p.status === 'In Progress').length;
      const completedProjects = projects.filter(p => p.status === 'Completed').length;
      const pendingInvoices = invoices.filter(i => i.status === 'Draft' || i.status === 'Sent').length;
      const paidInvoices = invoices.filter(i => i.status === 'Paid').length;

      setStats({
        totalClients: clients.length,
        totalProjects: projects.length,
        totalInvoices: invoices.length,
        totalRevenue,
        activeProjects,
        completedProjects,
        pendingInvoices,
        paidInvoices
      });

      setRecentProjects(projects.slice(0, 5));
      setRecentInvoices(invoices.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, icon: Icon, color, trend, trendValue, subtitle }) => (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-sm border border-gray-200 dark:border-gray-700 p-6 hover:shadow-md transition-all duration-300 group">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-gray-600 dark:text-gray-400 mb-1">{title}</p>
          <p className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
            {typeof value === 'number' ? value.toLocaleString() : value}
          </p>
          {subtitle && (
            <p className="text-sm text-gray-500 dark:text-gray-400">{subtitle}</p>
          )}
          {trend && (
            <div className="flex items-center mt-2">
              {trend === 'up' ? (
                <TrendingUp className="w-4 h-4 text-green-500 mr-1" />
              ) : (
                <TrendingDown className="w-4 h-4 text-red-500 mr-1" />
              )}
              <span className={`text-sm font-medium ${trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
                {trendValue}%
              </span>
            </div>
          )}
        </div>
        <div className={`p-3 rounded-xl ${color} group-hover:scale-110 transition-transform duration-300`}>
          <Icon className="w-6 h-6 text-white" />
        </div>
      </div>
    </div>
  );

  const QuickActionCard = ({ title, description, icon: Icon, href, color }) => (
    <Link
      to={href}
      className="block bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 p-6 hover:shadow-md hover:scale-105 transition-all duration-300 group"
    >
      <div className="flex items-center">
        <div className={`p-3 rounded-lg ${color} mr-4 group-hover:scale-110 transition-transform duration-300`}>
          <Icon className="w-6 h-6 text-white" />
        </div>
        <div className="flex-1">
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white group-hover:text-blue-600 transition-colors">
            {title}
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">{description}</p>
        </div>
        <ArrowRight className="w-5 h-5 text-gray-400 group-hover:text-blue-600 group-hover:translate-x-1 transition-all duration-300" />
      </div>
    </Link>
  );

  const RecentItem = ({ item, type, icon: Icon }) => (
    <div className="flex items-center justify-between p-4 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-lg transition-colors">
      <div className="flex items-center">
        <div className="p-2 bg-gray-100 dark:bg-gray-600 rounded-lg mr-3">
          <Icon className="w-4 h-4 text-gray-600 dark:text-gray-300" />
        </div>
        <div>
          <p className="font-medium text-gray-900 dark:text-white">{item.name}</p>
          <p className="text-sm text-gray-500 dark:text-gray-400">
            {type === 'project' ? item.client_name : item.project_name}
          </p>
        </div>
      </div>
      <div className="text-right">
        <p className="text-sm font-medium text-gray-900 dark:text-white">
          {type === 'invoice' ? `$${parseFloat(item.amount).toFixed(2)}` : item.status}
        </p>
        <p className="text-xs text-gray-500 dark:text-gray-400">
          {new Date(item.created_at).toLocaleDateString()}
        </p>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/4 mb-2"></div>
          <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="animate-pulse">
              <div className="h-32 bg-gray-200 dark:bg-gray-700 rounded-2xl"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Dashboard</h1>
          <p className="mt-2 text-gray-600 dark:text-gray-400">
            Welcome back! Here's what's happening with your business today.
          </p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="text-right">
            <p className="text-sm text-gray-500 dark:text-gray-400">Today</p>
            <p className="text-lg font-semibold text-gray-900 dark:text-white">
              {new Date().toLocaleDateString('en-US', { 
                weekday: 'long', 
                month: 'short', 
                day: 'numeric' 
              })}
            </p>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Clients"
          value={animatedStats.totalClients}
          icon={Users}
          color="bg-gradient-to-br from-blue-500 to-blue-600"
          trend="up"
          trendValue="12"
          subtitle="Active clients"
        />
        <StatCard
          title="Active Projects"
          value={animatedStats.activeProjects}
          icon={FolderOpen}
          color="bg-gradient-to-br from-green-500 to-green-600"
          trend="up"
          trendValue="8"
          subtitle={`${animatedStats.completedProjects} completed`}
        />
        <StatCard
          title="Total Revenue"
          value={`$${animatedStats.totalRevenue.toLocaleString()}`}
          icon={DollarSign}
          color="bg-gradient-to-br from-purple-500 to-purple-600"
          trend="up"
          trendValue="23"
          subtitle="This month"
        />
        <StatCard
          title="Pending Invoices"
          value={animatedStats.pendingInvoices}
          icon={FileText}
          color="bg-gradient-to-br from-orange-500 to-orange-600"
          trend="down"
          trendValue="5"
          subtitle={`${animatedStats.paidInvoices} paid`}
        />
      </div>

      {/* Quick Actions */}
      <div>
        <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <QuickActionCard
            title="Add New Client"
            description="Create a new client profile"
            icon={Users}
            href="/clients/new"
            color="bg-gradient-to-br from-blue-500 to-blue-600"
          />
          <QuickActionCard
            title="Start New Project"
            description="Create a new project"
            icon={FolderOpen}
            href="/projects/new"
            color="bg-gradient-to-br from-green-500 to-green-600"
          />
          <QuickActionCard
            title="Create Invoice"
            description="Generate a new invoice"
            icon={FileText}
            href="/invoices/new"
            color="bg-gradient-to-br from-purple-500 to-purple-600"
          />
        </div>
      </div>

      {/* Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Recent Projects */}
        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="p-6 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Recent Projects</h3>
              <Link
                to="/projects"
                className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center"
              >
                View all
                <ArrowRight className="w-4 h-4 ml-1" />
              </Link>
            </div>
          </div>
          <div className="p-6">
            {recentProjects.length === 0 ? (
              <div className="text-center py-8">
                <FolderOpen className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500 dark:text-gray-400">No projects yet</p>
                <Link
                  to="/projects/new"
                  className="inline-flex items-center mt-4 text-blue-600 hover:text-blue-700 font-medium"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Create your first project
                </Link>
              </div>
            ) : (
              <div className="space-y-2">
                {recentProjects.map((project) => (
                  <RecentItem
                    key={project.id}
                    item={project}
                    type="project"
                    icon={FolderOpen}
                  />
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Recent Invoices */}
        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="p-6 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Recent Invoices</h3>
              <Link
                to="/invoices"
                className="text-sm text-blue-600 hover:text-blue-700 font-medium flex items-center"
              >
                View all
                <ArrowRight className="w-4 h-4 ml-1" />
              </Link>
            </div>
          </div>
          <div className="p-6">
            {recentInvoices.length === 0 ? (
              <div className="text-center py-8">
                <FileText className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500 dark:text-gray-400">No invoices yet</p>
                <Link
                  to="/invoices/new"
                  className="inline-flex items-center mt-4 text-blue-600 hover:text-blue-700 font-medium"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Create your first invoice
                </Link>
              </div>
            ) : (
              <div className="space-y-2">
                {recentInvoices.map((invoice) => (
                  <RecentItem
                    key={invoice.id}
                    item={invoice}
                    type="invoice"
                    icon={FileText}
                  />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Performance Overview */}
      <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-sm border border-gray-200 dark:border-gray-700 p-6">
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-6">Performance Overview</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="text-center">
            <div className="w-16 h-16 bg-gradient-to-br from-green-500 to-green-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <Target className="w-8 h-8 text-white" />
            </div>
            <h4 className="text-2xl font-bold text-gray-900 dark:text-white">
              {Math.round((animatedStats.completedProjects / Math.max(animatedStats.totalProjects, 1)) * 100)}%
            </h4>
            <p className="text-sm text-gray-600 dark:text-gray-400">Project Completion Rate</p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <Award className="w-8 h-8 text-white" />
            </div>
            <h4 className="text-2xl font-bold text-gray-900 dark:text-white">
              {Math.round((animatedStats.paidInvoices / Math.max(animatedStats.totalInvoices, 1)) * 100)}%
            </h4>
            <p className="text-sm text-gray-600 dark:text-gray-400">Payment Success Rate</p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-gradient-to-br from-purple-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <Activity className="w-8 h-8 text-white" />
            </div>
            <h4 className="text-2xl font-bold text-gray-900 dark:text-white">
              {animatedStats.totalClients > 0 ? Math.round(animatedStats.totalRevenue / animatedStats.totalClients) : 0}
            </h4>
            <p className="text-sm text-gray-600 dark:text-gray-400">Avg Revenue per Client</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
