import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  Users, 
  FolderOpen, 
  FileText, 
  DollarSign, 
  TrendingUp, 
  TrendingDown,
  Calendar,
  Clock,
  CheckCircle,
  AlertCircle,
  Plus,
  ArrowUpRight,
  ArrowDownRight,
  BarChart3,
  PieChart,
  Activity,
  Target,
  Zap,
  Star,
  Award,
  Briefcase,
  CreditCard,
  Bell,
  Eye,
  Edit,
  Trash2
} from 'lucide-react';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalClients: 0,
    totalProjects: 0,
    totalInvoices: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
    overdueInvoices: 0,
    activeProjects: 0,
    completedProjects: 0
  });
  const [recentActivity, setRecentActivity] = useState([]);
  const [upcomingDeadlines, setUpcomingDeadlines] = useState([]);
  const [recentClients, setRecentClients] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [clientsRes, projectsRes, invoicesRes] = await Promise.all([
        axios.get('/api/clients'),
        axios.get('/api/projects'),
        axios.get('/api/invoices')
      ]);

      const clients = clientsRes.data.clients || [];
      const projects = projectsRes.data.projects || [];
      const invoices = invoicesRes.data.invoices || [];

      // Calculate statistics
      const totalRevenue = invoices
        .filter(inv => inv.status === 'Paid')
        .reduce((sum, inv) => sum + parseFloat(inv.amount || 0), 0);

      const paidInvoices = invoices.filter(inv => inv.status === 'Paid').length;
      const pendingInvoices = invoices.filter(inv => inv.status === 'Sent' || inv.status === 'Draft').length;
      const overdueInvoices = invoices.filter(inv => inv.status === 'Overdue').length;
      const activeProjects = projects.filter(proj => proj.status === 'In Progress').length;
      const completedProjects = projects.filter(proj => proj.status === 'Completed').length;

      setStats({
        totalClients: clients.length,
        totalProjects: projects.length,
        totalInvoices: invoices.length,
        totalRevenue,
        paidInvoices,
        pendingInvoices,
        overdueInvoices,
        activeProjects,
        completedProjects
      });

      // Recent activity (mock data for now)
      setRecentActivity([
        { id: 1, type: 'invoice', action: 'Invoice #INV-001 was paid', time: '2 hours ago', icon: CheckCircle, color: 'text-green-600' },
        { id: 2, type: 'project', action: 'Project "Website Redesign" was completed', time: '4 hours ago', icon: Target, color: 'text-blue-600' },
        { id: 3, type: 'client', action: 'New client "Acme Corp" was added', time: '1 day ago', icon: Users, color: 'text-purple-600' },
        { id: 4, type: 'invoice', action: 'Invoice #INV-002 was sent', time: '2 days ago', icon: FileText, color: 'text-orange-600' }
      ]);

      // Upcoming deadlines (mock data for now)
      setUpcomingDeadlines([
        { id: 1, title: 'Project: Mobile App Design', deadline: '2024-01-15', type: 'project', priority: 'high' },
        { id: 2, title: 'Invoice: #INV-003 Payment Due', deadline: '2024-01-18', type: 'invoice', priority: 'medium' },
        { id: 3, title: 'Client Meeting: Acme Corp', deadline: '2024-01-20', type: 'meeting', priority: 'low' }
      ]);

      // Recent clients
      setRecentClients(clients.slice(0, 5));

    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, change, changeType, icon: Icon, color, trend }) => (
    <div className="bg-white rounded-xl border border-gray-200 p-6 hover:shadow-lg transition-all duration-300">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-gray-600 mb-1">{title}</p>
          <p className="text-3xl font-bold text-gray-900 mb-2">{value}</p>
          {change && (
            <div className={`flex items-center text-sm ${changeType === 'increase' ? 'text-green-600' : 'text-red-600'}`}>
              {changeType === 'increase' ? <ArrowUpRight size={16} /> : <ArrowDownRight size={16} />}
              <span className="ml-1">{change}</span>
            </div>
          )}
        </div>
        <div className={`p-3 rounded-lg ${color}`}>
          <Icon size={24} className="text-white" />
        </div>
      </div>
    </div>
  );

  const ProgressCircle = ({ percentage, size = 120, strokeWidth = 8, color = "indigo" }) => {
    const radius = (size - strokeWidth) / 2;
    const circumference = radius * 2 * Math.PI;
    const strokeDasharray = circumference;
    const strokeDashoffset = circumference - (percentage / 100) * circumference;

    const colorClasses = {
      indigo: "stroke-indigo-600",
      green: "stroke-green-600",
      blue: "stroke-blue-600",
      purple: "stroke-purple-600"
    };

    return (
      <div className="relative inline-flex items-center justify-center">
        <svg width={size} height={size} className="transform -rotate-90">
          <circle
            cx={size / 2}
            cy={size / 2}
            r={radius}
            stroke="currentColor"
            strokeWidth={strokeWidth}
            fill="transparent"
            className="text-gray-200"
          />
          <circle
            cx={size / 2}
            cy={size / 2}
            r={radius}
            stroke="currentColor"
            strokeWidth={strokeWidth}
            fill="transparent"
            strokeDasharray={strokeDasharray}
            strokeDashoffset={strokeDashoffset}
            className={`${colorClasses[color]} transition-all duration-1000 ease-in-out`}
            strokeLinecap="round"
          />
        </svg>
        <div className="absolute inset-0 flex items-center justify-center">
          <span className="text-2xl font-bold text-gray-900">{percentage}%</span>
        </div>
      </div>
    );
  };

  const MiniChart = ({ data, color = "indigo" }) => {
    const max = Math.max(...data);
    const min = Math.min(...data);
    const range = max - min;
    
    const colorClasses = {
      indigo: "stroke-indigo-600 fill-indigo-50",
      green: "stroke-green-600 fill-green-50",
      blue: "stroke-blue-600 fill-blue-50",
      purple: "stroke-purple-600 fill-purple-50"
    };

    return (
      <div className="w-full h-16">
        <svg viewBox="0 0 100 40" className="w-full h-full">
          <polyline
            points={data.map((value, index) => 
              `${(index / (data.length - 1)) * 100},${40 - ((value - min) / range) * 30}`
            ).join(' ')}
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            className={colorClasses[color]}
          />
        </svg>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="space-y-8">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="bg-white rounded-xl border border-gray-200 p-6">
              <div className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded w-1/2 mb-2"></div>
                <div className="h-8 bg-gray-200 rounded w-3/4 mb-2"></div>
                <div className="h-4 bg-gray-200 rounded w-1/3"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold mb-1">Welcome back! 👋</h1>
            <p className="text-blue-100">Here's what's happening with your business today.</p>
          </div>
          <div className="hidden md:block">
            <div className="flex items-center space-x-6">
              <div className="text-center">
                <div className="text-xl font-bold">{stats.totalClients}</div>
                <div className="text-blue-200 text-xs">Active Clients</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-bold">{stats.activeProjects}</div>
                <div className="text-blue-200 text-xs">Active Projects</div>
              </div>
              <div className="text-center">
                <div className="text-xl font-bold">${stats.totalRevenue.toLocaleString()}</div>
                <div className="text-blue-200 text-xs">Total Revenue</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Clients"
          value={stats.totalClients}
          change="+12%"
          changeType="increase"
          icon={Users}
          color="bg-blue-500"
        />
        <StatCard
          title="Active Projects"
          value={stats.activeProjects}
          change="+8%"
          changeType="increase"
          icon={FolderOpen}
          color="bg-purple-500"
        />
        <StatCard
          title="Total Revenue"
          value={`$${stats.totalRevenue.toLocaleString()}`}
          change="+23%"
          changeType="increase"
          icon={DollarSign}
          color="bg-green-500"
        />
        <StatCard
          title="Paid Invoices"
          value={stats.paidInvoices}
          change="+5%"
          changeType="increase"
          icon={CheckCircle}
          color="bg-indigo-500"
        />
      </div>

      {/* Charts and Analytics */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Revenue Chart */}
        <div className="lg:col-span-2 bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-gray-900">Revenue Overview</h3>
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-indigo-600 rounded-full"></div>
              <span className="text-sm text-gray-600">Monthly Revenue</span>
            </div>
          </div>
          <div className="h-64">
            <MiniChart 
              data={[12000, 15000, 18000, 22000, 19000, 25000, 28000]} 
              color="indigo" 
            />
          </div>
        </div>

        {/* Project Progress */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-6">Project Progress</h3>
          <div className="space-y-6">
            <div className="text-center">
              <ProgressCircle percentage={75} color="indigo" />
              <p className="text-sm text-gray-600 mt-2">Overall Completion</p>
            </div>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Active Projects</span>
                <span className="text-sm font-medium">{stats.activeProjects}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Completed</span>
                <span className="text-sm font-medium">{stats.completedProjects}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Success Rate</span>
                <span className="text-sm font-medium text-green-600">92%</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Row */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Activity */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-gray-900">Recent Activity</h3>
            <Link to="/activity" className="text-sm text-indigo-600 hover:text-indigo-700">
              View all
            </Link>
          </div>
          <div className="space-y-4">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="flex items-start space-x-3">
                <div className={`p-2 rounded-lg bg-gray-100`}>
                  <activity.icon size={16} className={activity.color} />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-gray-900">{activity.action}</p>
                  <p className="text-xs text-gray-500">{activity.time}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Upcoming Deadlines */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-gray-900">Upcoming Deadlines</h3>
            <Link to="/calendar" className="text-sm text-indigo-600 hover:text-indigo-700">
              View calendar
            </Link>
          </div>
          <div className="space-y-4">
            {upcomingDeadlines.map((deadline) => (
              <div key={deadline.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex-1">
                  <p className="text-sm font-medium text-gray-900">{deadline.title}</p>
                  <p className="text-xs text-gray-500">{new Date(deadline.deadline).toLocaleDateString()}</p>
                </div>
                <span className={`px-2 py-1 text-xs rounded-full ${
                  deadline.priority === 'high' ? 'bg-red-100 text-red-800' :
                  deadline.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                  'bg-green-100 text-green-800'
                }`}>
                  {deadline.priority}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Recent Clients */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-gray-900">Recent Clients</h3>
            <Link to="/clients" className="text-sm text-indigo-600 hover:text-indigo-700">
              View all
            </Link>
          </div>
          <div className="space-y-4">
            {recentClients.map((client) => (
              <div key={client.id} className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-indigo-100 rounded-full flex items-center justify-center">
                  <span className="text-sm font-medium text-indigo-600">
                    {client.name?.charAt(0).toUpperCase()}
                  </span>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900">{client.name}</p>
                  <p className="text-xs text-gray-500">{client.company}</p>
                </div>
                <Link
                  to={`/clients/${client.id}`}
                  className="text-indigo-600 hover:text-indigo-700"
                >
                  <Eye size={16} />
                </Link>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-xl border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">Quick Actions</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Link
            to="/clients/new"
            className="flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-indigo-500 hover:bg-indigo-50 transition-colors group"
          >
            <div className="text-center">
              <Users size={32} className="mx-auto mb-2 text-gray-400 group-hover:text-indigo-600" />
              <p className="text-sm font-medium text-gray-600 group-hover:text-indigo-600">Add New Client</p>
            </div>
          </Link>
          <Link
            to="/projects/new"
            className="flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-indigo-500 hover:bg-indigo-50 transition-colors group"
          >
            <div className="text-center">
              <FolderOpen size={32} className="mx-auto mb-2 text-gray-400 group-hover:text-indigo-600" />
              <p className="text-sm font-medium text-gray-600 group-hover:text-indigo-600">Create Project</p>
            </div>
          </Link>
          <Link
            to="/invoices/new"
            className="flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-indigo-500 hover:bg-indigo-50 transition-colors group"
          >
            <div className="text-center">
              <FileText size={32} className="mx-auto mb-2 text-gray-400 group-hover:text-indigo-600" />
              <p className="text-sm font-medium text-gray-600 group-hover:text-indigo-600">Generate Invoice</p>
            </div>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
