import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { 
  Mail, 
  Lock, 
  Eye, 
  EyeOff, 
  Zap, 
  ArrowRight, 
  Shield, 
  CheckCircle, 
  Star, 
  Rocket, 
  Globe, 
  Sparkles,
  Users,
  TrendingUp,
  Award,
  ShieldCheck,
  Clock,
  Target
} from 'lucide-react';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [focusedField, setFocusedField] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  
  const { login } = useAuth();
  const navigate = useNavigate();

  // Animation trigger on component mount
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 300);
    return () => clearTimeout(timer);
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const result = await login(formData.email, formData.password);
    
    if (result.success) {
      navigate('/dashboard');
    }
    
    setLoading(false);
  };

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-purple-900 via-indigo-900 to-black">
      {/* Space Background with Celestial Elements */}
      <div className="absolute inset-0">
        {/* Stars */}
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(2px 2px at 20px 30px, #eee, transparent),
                           radial-gradient(2px 2px at 40px 70px, rgba(255,255,255,0.8), transparent),
                           radial-gradient(1px 1px at 90px 40px, #fff, transparent),
                           radial-gradient(1px 1px at 130px 80px, rgba(255,255,255,0.6), transparent),
                           radial-gradient(2px 2px at 160px 30px, #eee, transparent)`,
          backgroundRepeat: 'repeat',
          backgroundSize: '200px 100px'
        }}></div>

        {/* Large Planet with Rings */}
        <div className="absolute top-20 left-20 w-32 h-32 bg-gradient-to-br from-blue-300 to-blue-500 rounded-full shadow-2xl shadow-blue-500/30">
          <div className="absolute inset-0 rounded-full bg-gradient-to-br from-blue-200/50 to-transparent"></div>
          {/* Planet rings */}
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-48 h-48 border-2 border-blue-300/30 rounded-full"></div>
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-56 h-56 border border-blue-200/20 rounded-full"></div>
        </div>

        {/* Smaller Purple Moon */}
        <div className="absolute top-40 right-32 w-16 h-16 bg-gradient-to-br from-purple-400 to-purple-600 rounded-full shadow-xl shadow-purple-500/40">
          <div className="absolute inset-0 rounded-full bg-gradient-to-br from-purple-300/30 to-transparent"></div>
        </div>

        {/* Additional Celestial Bodies */}
        <div className="absolute top-60 left-1/3 w-8 h-8 bg-gradient-to-br from-cyan-300 to-cyan-500 rounded-full shadow-lg shadow-cyan-500/30"></div>
        <div className="absolute bottom-40 right-20 w-12 h-12 bg-gradient-to-br from-pink-300 to-pink-500 rounded-full shadow-lg shadow-pink-500/30"></div>

        {/* Meteor Streaks */}
        <div className="absolute top-32 right-1/4 w-1 h-20 bg-gradient-to-b from-transparent via-white to-transparent transform rotate-45 opacity-60 animate-pulse"></div>
        <div className="absolute bottom-32 left-1/4 w-1 h-16 bg-gradient-to-b from-transparent via-blue-200 to-transparent transform -rotate-45 opacity-40 animate-pulse" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/2 right-10 w-1 h-12 bg-gradient-to-b from-transparent via-purple-200 to-transparent transform rotate-12 opacity-50 animate-pulse" style={{animationDelay: '4s'}}></div>

        {/* Nebula Effects */}
        <div className="absolute top-0 right-0 w-96 h-96 bg-gradient-radial from-purple-500/10 via-transparent to-transparent rounded-full blur-3xl"></div>
        <div className="absolute bottom-0 left-0 w-80 h-80 bg-gradient-radial from-blue-500/10 via-transparent to-transparent rounded-full blur-3xl"></div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-6xl w-full grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          {/* Left Side - Welcome Message */}
          <div className="text-center lg:text-left space-y-6">
            <div className="space-y-4">
              <h1 className="text-5xl lg:text-6xl font-bold text-white leading-tight">
                SIGN IN TO YOUR
                <span className="block bg-gradient-to-r from-purple-400 to-blue-400 bg-clip-text text-transparent">
                  ADVENTURE!
                </span>
              </h1>
              <p className="text-xl text-gray-300 max-w-md mx-auto lg:mx-0">
                Join thousands of professionals exploring the universe of possibilities with our CRM platform.
              </p>
            </div>
            
            {/* Feature highlights */}
            <div className="space-y-3 pt-8">
              <div className="flex items-center space-x-3 text-gray-300">
                <CheckCircle className="w-5 h-5 text-green-400" />
                <span>Enterprise-grade security</span>
              </div>
              <div className="flex items-center space-x-3 text-gray-300">
                <CheckCircle className="w-5 h-5 text-green-400" />
                <span>24/7 customer support</span>
              </div>
              <div className="flex items-center space-x-3 text-gray-300">
                <CheckCircle className="w-5 h-5 text-green-400" />
                <span>Advanced analytics</span>
              </div>
            </div>
          </div>

          {/* Right Side - Login Form */}
          <div className="w-full max-w-md mx-auto">
            {/* Form Card */}
            <div className="bg-black/40 backdrop-blur-xl rounded-2xl p-8 border border-white/10 shadow-2xl">
              {/* Brand Logo */}
              <div className="flex items-center justify-center mb-8">
                <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-blue-500 rounded-xl flex items-center justify-center">
                  <Zap className="w-6 h-6 text-white" />
                </div>
                <span className="ml-3 text-2xl font-bold text-white">FreelancePro</span>
              </div>

              <form className="space-y-6" onSubmit={handleSubmit}>
                {/* Title */}
                <div className="text-center mb-8">
                  <h2 className="text-3xl font-bold text-white mb-2">SIGN IN</h2>
                  <p className="text-gray-400">Sign in with email address</p>
                </div>

                {/* Email Field */}
                <div className="space-y-2">
                  <div className="relative">
                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none z-10">
                      <Mail className={`h-5 w-5 transition-colors duration-200 ${
                        focusedField === 'email' ? 'text-purple-400' : 'text-gray-400'
                      }`} />
                    </div>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      autoComplete="email"
                      required
                      className={`w-full pl-12 pr-4 py-4 bg-gray-800/50 border-2 rounded-xl text-white placeholder-gray-400 transition-all duration-200 focus:outline-none focus:ring-0 ${
                        focusedField === 'email' 
                          ? 'border-purple-500 bg-gray-800/70' 
                          : 'border-gray-600 hover:border-gray-500 focus:border-purple-500'
                      }`}
                      placeholder="Yourname@gmail.com"
                      value={formData.email}
                      onChange={handleChange}
                      onFocus={() => setFocusedField('email')}
                      onBlur={() => setFocusedField(null)}
                    />
                  </div>
                </div>

                {/* Password Field */}
                <div className="space-y-2">
                  <div className="relative">
                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none z-10">
                      <Lock className={`h-5 w-5 transition-colors duration-200 ${
                        focusedField === 'password' ? 'text-purple-400' : 'text-gray-400'
                      }`} />
                    </div>
                    <input
                      id="password"
                      name="password"
                      type={showPassword ? 'text' : 'password'}
                      autoComplete="current-password"
                      required
                      className={`w-full pl-12 pr-12 py-4 bg-gray-800/50 border-2 rounded-xl text-white placeholder-gray-400 transition-all duration-200 focus:outline-none focus:ring-0 ${
                        focusedField === 'password' 
                          ? 'border-purple-500 bg-gray-800/70' 
                          : 'border-gray-600 hover:border-gray-500 focus:border-purple-500'
                      }`}
                      placeholder="Enter your password"
                      value={formData.password}
                      onChange={handleChange}
                      onFocus={() => setFocusedField('password')}
                      onBlur={() => setFocusedField(null)}
                    />
                    <button
                      type="button"
                      className="absolute inset-y-0 right-0 pr-4 flex items-center z-10 text-gray-400 hover:text-gray-300 transition-colors duration-200"
                      onClick={() => setShowPassword(!showPassword)}
                    >
                      {showPassword ? (
                        <EyeOff className="h-5 w-5" />
                      ) : (
                        <Eye className="h-5 w-5" />
                      )}
                    </button>
                  </div>
                </div>

                {/* Sign In Button */}
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full flex items-center justify-center px-6 py-4 bg-gradient-to-r from-purple-600 to-blue-600 text-white font-semibold text-lg rounded-xl shadow-xl hover:shadow-2xl transform hover:scale-[1.02] transition-all duration-200 focus:outline-none focus:ring-4 focus:ring-purple-500/20 disabled:opacity-70 disabled:cursor-not-allowed disabled:transform-none"
                >
                  {loading ? (
                    <div className="flex items-center space-x-2">
                      <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                      <span>Signing in...</span>
                    </div>
                  ) : (
                    <span>Sign in</span>
                  )}
                </button>

                {/* Separator */}
                <div className="relative">
                  <div className="absolute inset-0 flex items-center">
                    <div className="w-full border-t border-gray-600"></div>
                  </div>
                  <div className="relative flex justify-center text-sm">
                    <span className="px-2 bg-transparent text-gray-400">Or continue with</span>
                  </div>
                </div>

                {/* Social Login Buttons */}
                <div className="grid grid-cols-2 gap-3">
                  <button
                    type="button"
                    className="flex items-center justify-center px-4 py-3 space-btn-secondary"
                  >
                    <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
                      <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                      <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                      <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                      <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                    </svg>
                    Google
                  </button>
                  <button
                    type="button"
                    className="flex items-center justify-center px-4 py-3 bg-blue-600 text-white font-medium rounded-xl hover:bg-blue-700 transition-colors duration-200"
                  >
                    <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                    </svg>
                    Facebook
                  </button>
                </div>

                {/* Terms */}
                <p className="text-xs text-gray-400 text-center">
                  By signing in you agree to our{' '}
                  <Link to="/terms" className="text-purple-400 hover:text-purple-300 underline">
                    Terms and Conditions
                  </Link>
                </p>

                {/* Register Link */}
                <div className="text-center pt-4">
                  <p className="text-gray-400">
                    Don't have an account?{' '}
                    <Link
                      to="/register"
                      className="text-purple-400 hover:text-purple-300 font-semibold transition-colors duration-200"
                    >
                      Create one here
                    </Link>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>

      {/* Custom CSS for enhanced effects */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-10px) rotate(1deg); }
        }
        
        .animate-float {
          animation: float 6s ease-in-out infinite;
        }
        
        .bg-gradient-radial {
          background: radial-gradient(circle, var(--tw-gradient-stops));
        }
      `}</style>
    </div>
  );
};

export default Login;
