import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FolderOpen, 
  Plus, 
  Search, 
  Eye, 
  Edit, 
  Trash2, 
  Calendar,
  User,
  DollarSign,
  Grid3X3,
  List,
  Filter,
  SortAsc,
  SortDesc,
  Clock,
  CheckCircle,
  AlertCircle,
  MoreHorizontal
} from 'lucide-react';
import Modal from '../components/ui/Modal';

const Projects = () => {
  // Sample data for demonstration
  const sampleProjects = [
    {
      id: 1,
      name: 'Website Redesign',
      client: 'Acme Corp',
      clientId: 1,
      dueDate: '2024-01-15',
      status: 'in-progress',
      priority: 'high',
      budget: 15000,
      progress: 65,
      description: 'Complete redesign of the company website with modern UI/UX'
    },
    {
      id: 2,
      name: 'Mobile App Development',
      client: 'TechFlow Innovations',
      clientId: 2,
      dueDate: '2024-02-20',
      status: 'in-progress',
      priority: 'medium',
      budget: 25000,
      progress: 30,
      description: 'Native mobile app for iOS and Android platforms'
    },
    {
      id: 3,
      name: 'Brand Identity Package',
      client: 'StartupXYZ',
      clientId: 3,
      dueDate: '2024-01-10',
      status: 'completed',
      priority: 'low',
      budget: 8000,
      progress: 100,
      description: 'Complete brand identity including logo, colors, and guidelines'
    },
    {
      id: 4,
      name: 'E-commerce Platform',
      client: 'RetailPlus',
      clientId: 4,
      dueDate: '2023-12-30',
      status: 'overdue',
      priority: 'high',
      budget: 35000,
      progress: 80,
      description: 'Full e-commerce solution with payment integration'
    },
    {
      id: 5,
      name: 'Marketing Campaign',
      client: 'Johnson Marketing',
      clientId: 3,
      dueDate: '2024-03-15',
      status: 'todo',
      priority: 'medium',
      budget: 12000,
      progress: 0,
      description: 'Digital marketing campaign for product launch'
    },
    {
      id: 6,
      name: 'Database Migration',
      client: 'Acme Corp',
      clientId: 1,
      dueDate: '2024-02-05',
      status: 'todo',
      priority: 'low',
      budget: 18000,
      progress: 0,
      description: 'Migrate legacy database to cloud infrastructure'
    }
  ];

  const [projects, setProjects] = useState(sampleProjects);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState('table'); // 'table' or 'kanban'
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('dueDate');
  const [sortOrder, setSortOrder] = useState('asc');
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);

  useEffect(() => {
    setLoading(false);
  }, []);

  const handleDelete = async (project) => {
    if (window.confirm(`Are you sure you want to delete "${project.name}"?`)) {
      try {
        // await api.delete(`/projects/${project.id}`);
        setProjects(projects.filter(p => p.id !== project.id));
        // toast.success('Project deleted successfully');
      } catch (error) {
        console.error('Error deleting project:', error);
        // toast.error('Failed to delete project');
      }
    }
  };

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const filteredProjects = projects
    .filter(project => {
      const matchesSearch = 
        project.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        project.client?.toLowerCase().includes(searchTerm.toLowerCase());
      return matchesSearch;
    })
    .sort((a, b) => {
      let aValue = a[sortBy];
      let bValue = b[sortBy];
      
      if (sortBy === 'dueDate') {
        aValue = new Date(aValue);
        bValue = new Date(bValue);
      }
      
      if (sortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

  const getStatusBadge = (status) => {
    const styles = {
      'todo': 'bg-gray-100 text-gray-700',
      'in-progress': 'bg-warning-100 text-warning-700',
      'completed': 'bg-success-100 text-success-700',
      'overdue': 'bg-error-100 text-error-700'
    };
    
    const labels = {
      'todo': 'To Do',
      'in-progress': 'In Progress',
      'completed': 'Completed',
      'overdue': 'Overdue'
    };

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${styles[status]}`}>
        {labels[status]}
      </span>
    );
  };

  const getPriorityColor = (priority) => {
    const colors = {
      high: 'text-error-600',
      medium: 'text-warning-600',
      low: 'text-success-600'
    };
    return colors[priority] || 'text-gray-600';
  };

  const getSortIcon = (column) => {
    if (sortBy !== column) {
      return <SortAsc className="w-4 h-4 text-gray-400" />;
    }
    return sortOrder === 'asc' ? 
      <SortAsc className="w-4 h-4 text-gray-600" /> : 
      <SortDesc className="w-4 h-4 text-gray-600" />;
  };

  const getProjectsByStatus = (status) => {
    return filteredProjects.filter(project => project.status === status);
  };

  const renderKanbanView = () => {
    const columns = [
      { id: 'todo', title: 'Mission Briefing', color: 'gray' },
      { id: 'in-progress', title: 'In Progress', color: 'warning' },
      { id: 'completed', title: 'Mission Complete', color: 'success' }
    ];

    return (
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {columns.map((column) => (
          <div key={column.id} className="space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="font-semibold space-text-primary text-lg">{column.title}</h3>
              <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                column.color === 'gray' ? 'bg-gray-500/20 text-gray-300' :
                column.color === 'warning' ? 'bg-yellow-500/20 text-yellow-300' :
                'bg-green-500/20 text-green-300'
              }`}>
                {getProjectsByStatus(column.id).length}
              </span>
            </div>
            <div className="space-y-3">
              {getProjectsByStatus(column.id).map((project) => (
                <div
                  key={project.id}
                  className="space-card space-card-hover p-4 cursor-pointer group"
                  onClick={() => setSelectedProject(project)}
                >
                  <div className="space-y-3">
                    <div className="flex items-start justify-between">
                      <h4 className="font-semibold space-text-primary group-hover:text-purple-300 transition-colors">
                        {project.name}
                      </h4>
                      <div className={`w-3 h-3 rounded-full ${
                        project.priority === 'high' ? 'bg-red-500' :
                        project.priority === 'medium' ? 'bg-yellow-500' : 'bg-green-500'
                      }`} />
                    </div>

                    <div className="flex items-center gap-2 text-sm space-text-secondary">
                      <User className="w-4 h-4" />
                      <span>{project.client}</span>
                    </div>

                    <div className="flex items-center gap-2 text-sm space-text-secondary">
                      <Calendar className="w-4 h-4" />
                      <span>Due {new Date(project.dueDate).toLocaleDateString()}</span>
                    </div>

                    <div className="flex items-center gap-2 text-sm space-text-secondary">
                      <DollarSign className="w-4 h-4" />
                      <span>${project.budget.toLocaleString()}</span>
                    </div>

                    {project.status === 'in-progress' && (
                      <div className="space-y-2">
                        <div className="flex items-center justify-between text-sm">
                          <span className="space-text-muted">Progress</span>
                          <span className="font-semibold space-text-primary">{project.progress}%</span>
                        </div>
                        <div className="w-full bg-gray-700 rounded-full h-2">
                          <div
                            className="bg-gradient-to-r from-purple-500 to-blue-500 h-2 rounded-full transition-all"
                            style={{ width: `${project.progress}%` }}
                          />
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    );
  };

  const renderTableView = () => (
    <div className="card">
      <div className="overflow-x-auto">
        <table className="table">
          <thead>
            <tr>
              <th 
                className="cursor-pointer hover:bg-gray-100 transition-colors"
                onClick={() => handleSort('name')}
              >
                <div className="flex items-center gap-2">
                  <span>Project Name</span>
                  {getSortIcon('name')}
                </div>
              </th>
              <th 
                className="cursor-pointer hover:bg-gray-100 transition-colors"
                onClick={() => handleSort('client')}
              >
                <div className="flex items-center gap-2">
                  <span>Client Name</span>
                  {getSortIcon('client')}
                </div>
              </th>
              <th 
                className="cursor-pointer hover:bg-gray-100 transition-colors"
                onClick={() => handleSort('dueDate')}
              >
                <div className="flex items-center gap-2">
                  <span>Due Date</span>
                  {getSortIcon('dueDate')}
                </div>
              </th>
              <th>Status</th>
              <th>Priority</th>
              <th>Budget</th>
              <th>Progress</th>
              <th className="text-right">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredProjects.map((project) => (
              <tr key={project.id} className="hover:bg-gray-50 transition-colors">
                <td>
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-gradient-to-br from-primary-500 to-primary-600 rounded-lg flex items-center justify-center">
                      <FolderOpen className="w-5 h-5 text-white" />
                    </div>
                    <div>
                      <div className="font-medium text-gray-900">{project.name}</div>
                      <div className="text-sm text-gray-500 truncate max-w-48">{project.description}</div>
                    </div>
                  </div>
                </td>
                <td>
                  <div className="flex items-center gap-2">
                    <User className="w-4 h-4 text-gray-400" />
                    <span className="text-sm text-gray-900">{project.client}</span>
                  </div>
                </td>
                <td>
                  <div className="flex items-center gap-2">
                    <Calendar className="w-4 h-4 text-gray-400" />
                    <span className="text-sm text-gray-900">
                      {new Date(project.dueDate).toLocaleDateString()}
                    </span>
                  </div>
                </td>
                <td>
                  {getStatusBadge(project.status)}
                </td>
                <td>
                  <span className={`text-sm font-medium ${getPriorityColor(project.priority)}`}>
                    {project.priority}
                  </span>
                </td>
                <td>
                  <div className="flex items-center gap-1">
                    <DollarSign className="w-4 h-4 text-gray-400" />
                    <span className="font-semibold text-gray-900">
                      ${project.budget.toLocaleString()}
                    </span>
                  </div>
                </td>
                <td>
                  <div className="flex items-center gap-2">
                    <div className="w-16 bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-primary-600 h-2 rounded-full"
                        style={{ width: `${project.progress}%` }}
                      />
                    </div>
                    <span className="text-sm text-gray-600">{project.progress}%</span>
                  </div>
                </td>
                <td>
                  <div className="flex items-center justify-end gap-2">
                    <button
                      onClick={() => setSelectedProject(project)}
                      className="p-2 text-gray-400 hover:text-primary-600 hover:bg-primary-50 rounded-lg transition-colors"
                      title="View"
                    >
                      <Eye className="w-4 h-4" />
                    </button>
                    <Link
                      to={`/projects/${project.id}/edit`}
                      className="p-2 text-gray-400 hover:text-success-600 hover:bg-success-50 rounded-lg transition-colors"
                      title="Edit"
                    >
                      <Edit className="w-4 h-4" />
                    </Link>
                    <button
                      onClick={() => handleDelete(project)}
                      className="p-2 text-gray-400 hover:text-error-600 hover:bg-error-50 rounded-lg transition-colors"
                      title="Delete"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto space-y-6">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-1/4 mb-2"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2"></div>
          </div>
          <div className="card">
            <div className="card-body">
              <div className="animate-pulse space-y-4">
                {[...Array(5)].map((_, i) => (
                  <div key={i} className="h-16 bg-gray-200 rounded"></div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <div className="max-w-7xl mx-auto p-3 space-y-3">
        {/* Page Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold space-text-primary">Mission Control</h1>
            <p className="space-text-secondary text-sm">Navigate your project universe and track mission progress</p>
          </div>
          <button
            onClick={() => setShowAddModal(true)}
            className="space-btn flex items-center gap-2 px-6 py-3"
          >
            <Plus className="w-4 h-4" />
            Launch New Project
          </button>
        </div>

        {/* Search, Filters, and View Toggle */}
        <div className="flex items-center justify-between">
          <div className="relative flex-1 max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search projects..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="space-input pl-10"
            />
          </div>

          <div className="flex items-center gap-3">
            <button className="space-btn-secondary flex items-center gap-2 px-4 py-2">
              <Filter className="w-4 h-4" />
              Filters
            </button>

            <div className="flex items-center space-card p-1">
              <button
                onClick={() => setViewMode('table')}
                className={`p-2 rounded-lg transition-colors ${
                  viewMode === 'table'
                    ? 'bg-purple-500/20 text-purple-300'
                    : 'text-gray-400 hover:text-white'
                }`}
                title="Table View"
              >
                <List className="w-4 h-4" />
              </button>
              <button
                onClick={() => setViewMode('kanban')}
                className={`p-2 rounded-lg transition-colors ${
                  viewMode === 'kanban'
                    ? 'bg-purple-500/20 text-purple-300'
                    : 'text-gray-400 hover:text-white'
                }`}
                title="Kanban View"
              >
                <Grid3X3 className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>

        {/* Projects Content */}
        {viewMode === 'table' ? renderTableView() : renderKanbanView()}

        {/* Empty State */}
        {filteredProjects.length === 0 && (
          <div className="text-center py-12">
            <FolderOpen className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No projects found</h3>
            <p className="text-gray-600 mb-6">
              {searchTerm 
                ? 'Try adjusting your search criteria.' 
                : 'Get started by creating your first project.'
              }
            </p>
            {!searchTerm && (
              <button
                onClick={() => setShowAddModal(true)}
                className="btn btn-primary"
              >
                <Plus className="w-4 h-4 mr-2" />
                Create Project
              </button>
            )}
          </div>
        )}
      </div>

      {/* Add Project Modal */}
      <Modal
        isOpen={showAddModal}
        onClose={() => setShowAddModal(false)}
        title="Create New Project"
        size="lg"
      >
        <form className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="form-label">Project Name</label>
              <input
                type="text"
                className="form-input"
                placeholder="Enter project name"
              />
            </div>
            <div>
              <label className="form-label">Client</label>
              <select className="form-input">
                <option>Select a client</option>
                <option>Acme Corp</option>
                <option>TechFlow Innovations</option>
                <option>StartupXYZ</option>
              </select>
            </div>
          </div>
          
          <div>
            <label className="form-label">Description</label>
            <textarea
              className="form-input"
              rows="3"
              placeholder="Describe the project..."
            />
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="form-label">Due Date</label>
              <input
                type="date"
                className="form-input"
              />
            </div>
            <div>
              <label className="form-label">Priority</label>
              <select className="form-input">
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
              </select>
            </div>
            <div>
              <label className="form-label">Budget</label>
              <input
                type="number"
                className="form-input"
                placeholder="0"
              />
            </div>
          </div>
          
          <div className="flex items-center justify-end gap-3 pt-4">
            <button
              type="button"
              onClick={() => setShowAddModal(false)}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
            >
              Create Project
            </button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default Projects;
