import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { 
  Users, 
  FolderOpen, 
  FileText, 
  DollarSign,
  TrendingUp,
  Clock,
  CheckCircle,
  AlertCircle
} from 'lucide-react';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalClients: 0,
    totalProjects: 0,
    totalInvoices: 0,
    totalRevenue: 0,
    activeProjects: 0,
    completedProjects: 0,
    overdueProjects: 0,
    pendingInvoices: 0,
  });
  const [recentProjects, setRecentProjects] = useState([]);
  const [recentInvoices, setRecentInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [clientsRes, projectsRes, invoicesRes] = await Promise.all([
        axios.get('/api/clients'),
        axios.get('/api/projects'),
        axios.get('/api/invoices'),
      ]);

      const clients = clientsRes.data.clients;
      const projects = projectsRes.data.projects;
      const invoices = invoicesRes.data.invoices;

      // Calculate stats
      const totalClients = clients.length;
      const totalProjects = projects.length;
      const totalInvoices = invoices.length;
      
      const activeProjects = projects.filter(p => p.status === 'In Progress').length;
      const completedProjects = projects.filter(p => p.status === 'Completed').length;
      const overdueProjects = projects.filter(p => p.status === 'Overdue').length;
      
      const totalRevenue = invoices
        .filter(i => i.status === 'Paid')
        .reduce((sum, invoice) => sum + parseFloat(invoice.amount), 0);
      
      const pendingInvoices = invoices.filter(i => i.status === 'Sent' || i.status === 'Draft').length;

      setStats({
        totalClients,
        totalProjects,
        totalInvoices,
        totalRevenue,
        activeProjects,
        completedProjects,
        overdueProjects,
        pendingInvoices,
      });

      // Get recent projects (last 5)
      setRecentProjects(projects.slice(0, 5));

      // Get recent invoices (last 5)
      setRecentInvoices(invoices.slice(0, 5));

    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      'In Progress': { class: 'badge-info', icon: Clock },
      'Completed': { class: 'badge-success', icon: CheckCircle },
      'Overdue': { class: 'badge-danger', icon: AlertCircle },
      'On Hold': { class: 'badge-warning', icon: Clock },
    };

    const config = statusConfig[status] || statusConfig['In Progress'];
    const Icon = config.icon;

    return (
      <span className={`badge ${config.class} flex items-center gap-1`}>
        <Icon size={12} />
        {status}
      </span>
    );
  };

  const getInvoiceStatusBadge = (status) => {
    const statusConfig = {
      'Draft': { class: 'badge-warning' },
      'Sent': { class: 'badge-info' },
      'Paid': { class: 'badge-success' },
      'Overdue': { class: 'badge-danger' },
    };

    const config = statusConfig[status] || statusConfig['Draft'];

    return (
      <span className={`badge ${config.class}`}>
        {status}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="loading">
        <div>Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center lg:text-left">
        <h1 className="text-4xl font-bold text-gray-900 mb-2">Dashboard</h1>
        <p className="text-lg text-gray-600">
          Welcome back! Here's an overview of your business performance.
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="card group">
          <div className="card-body">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Total Clients</p>
                <p className="text-3xl font-bold text-gray-900 mt-2">{stats.totalClients}</p>
              </div>
              <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center group-hover:scale-110 transition-transform duration-200">
                <Users className="h-8 w-8 text-white" />
              </div>
            </div>
          </div>
        </div>

        <div className="card group">
          <div className="card-body">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Total Projects</p>
                <p className="text-3xl font-bold text-gray-900 mt-2">{stats.totalProjects}</p>
              </div>
              <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-green-600 rounded-2xl flex items-center justify-center group-hover:scale-110 transition-transform duration-200">
                <FolderOpen className="h-8 w-8 text-white" />
              </div>
            </div>
          </div>
        </div>

        <div className="card group">
          <div className="card-body">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Total Invoices</p>
                <p className="text-3xl font-bold text-gray-900 mt-2">{stats.totalInvoices}</p>
              </div>
              <div className="w-16 h-16 bg-gradient-to-r from-purple-500 to-purple-600 rounded-2xl flex items-center justify-center group-hover:scale-110 transition-transform duration-200">
                <FileText className="h-8 w-8 text-white" />
              </div>
            </div>
          </div>
        </div>

        <div className="card group">
          <div className="card-body">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-semibold text-gray-600 uppercase tracking-wide">Total Revenue</p>
                <p className="text-3xl font-bold text-gray-900 mt-2">${stats.totalRevenue.toFixed(2)}</p>
              </div>
              <div className="w-16 h-16 bg-gradient-to-r from-yellow-500 to-yellow-600 rounded-2xl flex items-center justify-center group-hover:scale-110 transition-transform duration-200">
                <DollarSign className="h-8 w-8 text-white" />
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Project Status Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="card-body">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <TrendingUp className="h-8 w-8 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Active Projects</p>
                <p className="text-2xl font-bold text-gray-900">{stats.activeProjects}</p>
              </div>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="card-body">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <CheckCircle className="h-8 w-8 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed</p>
                <p className="text-2xl font-bold text-gray-900">{stats.completedProjects}</p>
              </div>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="card-body">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <AlertCircle className="h-8 w-8 text-red-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Overdue</p>
                <p className="text-2xl font-bold text-gray-900">{stats.overdueProjects}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Projects */}
        <div className="card">
          <div className="card-header">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-medium text-gray-900">Recent Projects</h3>
              <Link to="/projects" className="text-sm text-blue-600 hover:text-blue-500">
                View all
              </Link>
            </div>
          </div>
          <div className="card-body">
            {recentProjects.length === 0 ? (
              <p className="text-gray-500 text-center py-4">No projects yet</p>
            ) : (
              <div className="space-y-4">
                {recentProjects.map((project) => (
                  <div key={project.id} className="flex items-center justify-between">
                    <div className="flex-1">
                      <p className="text-sm font-medium text-gray-900">{project.name}</p>
                      <p className="text-sm text-gray-500">{project.client_name}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      {getStatusBadge(project.status)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Recent Invoices */}
        <div className="card">
          <div className="card-header">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-medium text-gray-900">Recent Invoices</h3>
              <Link to="/invoices" className="text-sm text-blue-600 hover:text-blue-500">
                View all
              </Link>
            </div>
          </div>
          <div className="card-body">
            {recentInvoices.length === 0 ? (
              <p className="text-gray-500 text-center py-4">No invoices yet</p>
            ) : (
              <div className="space-y-4">
                {recentInvoices.map((invoice) => (
                  <div key={invoice.id} className="flex items-center justify-between">
                    <div className="flex-1">
                      <p className="text-sm font-medium text-gray-900">{invoice.invoice_number}</p>
                      <p className="text-sm text-gray-500">{invoice.client_name}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm font-medium text-gray-900">
                        ${parseFloat(invoice.amount).toFixed(2)}
                      </span>
                      {getInvoiceStatusBadge(invoice.status)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900">Quick Actions</h3>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Link
              to="/clients"
              className="btn btn-outline w-full justify-center"
            >
              <Users size={20} />
              Manage Clients
            </Link>
            <Link
              to="/projects"
              className="btn btn-outline w-full justify-center"
            >
              <FolderOpen size={20} />
              Manage Projects
            </Link>
            <Link
              to="/invoices"
              className="btn btn-outline w-full justify-center"
            >
              <FileText size={20} />
              Manage Invoices
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
