import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../config/axios';
import toast from 'react-hot-toast';
import { 
  Users, 
  Plus, 
  Eye, 
  Edit, 
  Trash2, 
  Mail,
  Phone
} from 'lucide-react';
import DataTable from '../components/ui/DataTable';
import StatusBadge from '../components/ui/StatusBadge';

const Clients = () => {
  // Sample data for demonstration
  const sampleClients = [
    {
      id: 1,
      name: 'Lisa Rodriguez',
      company: 'Verde Sustainability Consulting',
      email: 'lisa.rodriguez@verdesustain.com',
      phone: '+1-555-4567',
      status: 'active',
      added: '9/19/2025',
      created_at: '2025-09-19',
      projects: 3,
      revenue: 15000
    },
    {
      id: 2,
      name: 'Michael Chen',
      company: 'TechFlow Innovations',
      email: 'm.chen@techflow.io',
      phone: '+1-555-7890',
      status: 'active',
      added: '9/19/2025',
      created_at: '2025-09-19',
      projects: 2,
      revenue: 12000
    },
    {
      id: 3,
      name: 'Sarah Johnson',
      company: 'Johnson Marketing Solutions',
      email: 'sarah@johnsonmarketing.com',
      phone: '+1-555-0123',
      status: 'pending',
      added: '9/19/2025',
      created_at: '2025-09-19',
      projects: 1,
      revenue: 8000
    },
    {
      id: 4,
      name: 'Ann Smith',
      company: 'Acme Corporation',
      email: 'ann.smith@acmecorp.com',
      phone: '+1-555-123-4567',
      status: 'active',
      added: '9/19/2025',
      created_at: '2025-09-19',
      projects: 4,
      revenue: 25000
    }
  ];

  const [clients, setClients] = useState(sampleClients);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Simulate loading
    setLoading(false);
  }, []);

  const handleDelete = async (client) => {
    if (window.confirm('Are you sure you want to delete this client?')) {
      try {
        await api.delete(`/clients/${client.id}`);
        setClients(clients.filter(c => c.id !== client.id));
        toast.success('Client deleted successfully');
      } catch (error) {
        console.error('Error deleting client:', error);
        toast.error('Failed to delete client');
      }
    }
  };

  const columns = [
    {
      key: 'name',
      title: 'Client',
      render: (value, row) => (
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-full flex items-center justify-center">
            <span className="text-sm font-semibold text-white">
              {row.name?.charAt(0).toUpperCase()}
            </span>
          </div>
          <div>
            <div className="font-medium text-gray-900">{row.name}</div>
            <div className="text-sm text-gray-500">{row.company}</div>
          </div>
        </div>
      )
    },
    {
      key: 'email',
      title: 'Contact',
      render: (value, row) => (
        <div className="space-y-1">
          <div className="flex items-center text-sm text-gray-900">
            <Mail className="w-3 h-3 mr-2 text-gray-400" />
            {row.email}
          </div>
          <div className="flex items-center text-sm text-gray-600">
            <Phone className="w-3 h-3 mr-2 text-gray-400" />
            {row.phone}
          </div>
        </div>
      )
    },
    {
      key: 'status',
      title: 'Status',
      render: (value) => <StatusBadge status={value} />
    },
    {
      key: 'projects',
      title: 'Projects',
      render: (value) => (
        <div className="text-center">
          <div className="text-lg font-semibold text-gray-900">{value}</div>
        </div>
      )
    },
    {
      key: 'revenue',
      title: 'Revenue',
      type: 'currency',
      render: (value) => (
        <div className="text-right">
          <div className="font-semibold text-gray-900">
            ${value.toLocaleString()}
          </div>
        </div>
      )
    },
    {
      key: 'created_at',
      title: 'Added',
      type: 'date',
      render: (value) => new Date(value).toLocaleDateString()
    }
  ];

  const actions = [
    {
      icon: <Eye className="w-4 h-4" />,
      title: 'View',
      onClick: (client) => window.location.href = `/clients/${client.id}`
    },
    {
      icon: <Edit className="w-4 h-4" />,
      title: 'Edit',
      onClick: (client) => window.location.href = `/clients/${client.id}/edit`
    },
    {
      icon: <Trash2 className="w-4 h-4" />,
      title: 'Delete',
      onClick: handleDelete
    }
  ];

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </div>
        <div className="card">
          <div className="card-body">
            <div className="animate-pulse space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-16 bg-gray-200 rounded"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Clients</h1>
          <p className="text-gray-600 mt-1">Manage your client relationships and track their projects</p>
        </div>
        <Link
          to="/clients/new"
          className="btn btn-primary flex items-center space-x-2"
        >
          <Plus className="w-4 h-4" />
          <span>Add Client</span>
        </Link>
      </div>

      {/* Clients Table */}
      <DataTable
        data={clients}
        columns={columns}
        actions={actions}
        searchable={true}
        sortable={true}
        filterable={true}
        loading={loading}
        onRowClick={(client) => window.location.href = `/clients/${client.id}`}
      />
    </div>
  );
};

export default Clients;
