import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Plus, Search, Edit, Trash2, Eye, Calendar, DollarSign, Clock, CheckCircle, AlertCircle, Pause } from 'lucide-react';
import { format, parseISO, isAfter, isBefore } from 'date-fns';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [projectToDelete, setProjectToDelete] = useState(null);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('/api/projects');
      setProjects(response.data.projects);
    } catch (error) {
      console.error('Error fetching projects:', error);
      toast.error('Failed to fetch projects');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (projectId) => {
    try {
      await axios.delete(`/api/projects/${projectId}`);
      setProjects(projects.filter(project => project.id !== projectId));
      toast.success('Project deleted successfully');
      setShowDeleteModal(false);
      setProjectToDelete(null);
    } catch (error) {
      console.error('Error deleting project:', error);
      toast.error(error.response?.data?.message || 'Failed to delete project');
    }
  };

  const handleStatusChange = async (projectId, newStatus) => {
    try {
      const response = await axios.patch(`/api/projects/${projectId}/status`, {
        status: newStatus
      });
      
      setProjects(projects.map(project => 
        project.id === projectId 
          ? { ...project, status: newStatus }
          : project
      ));
      
      toast.success('Project status updated successfully');
    } catch (error) {
      console.error('Error updating project status:', error);
      toast.error('Failed to update project status');
    }
  };

  const getStatusBadge = (status, dueDate) => {
    const statusConfig = {
      'In Progress': { 
        class: 'badge-info', 
        icon: Clock,
        color: 'text-blue-600'
      },
      'Completed': { 
        class: 'badge-success', 
        icon: CheckCircle,
        color: 'text-green-600'
      },
      'Overdue': { 
        class: 'badge-danger', 
        icon: AlertCircle,
        color: 'text-red-600'
      },
      'On Hold': { 
        class: 'badge-warning', 
        icon: Pause,
        color: 'text-yellow-600'
      },
    };

    const config = statusConfig[status] || statusConfig['In Progress'];
    const Icon = config.icon;

    // Check if project is overdue
    let actualStatus = status;
    if (status === 'In Progress' && dueDate) {
      const due = parseISO(dueDate);
      const now = new Date();
      if (isBefore(due, now)) {
        actualStatus = 'Overdue';
        const overdueConfig = statusConfig['Overdue'];
        return (
          <span className={`badge ${overdueConfig.class} flex items-center gap-1`}>
            <overdueConfig.icon size={12} />
            Overdue
          </span>
        );
      }
    }

    return (
      <span className={`badge ${config.class} flex items-center gap-1`}>
        <Icon size={12} />
        {actualStatus}
      </span>
    );
  };

  const filteredProjects = projects.filter(project => {
    const matchesSearch = 
      project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      project.client_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
    
    const matchesStatus = !statusFilter || project.status === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  if (loading) {
    return (
      <div className="loading">
        <div>Loading projects...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Projects</h1>
          <p className="mt-1 text-sm text-gray-600">
            Track and manage your client projects.
          </p>
        </div>
        <Link to="/projects/new" className="btn btn-primary">
          <Plus size={20} />
          Add Project
        </Link>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Search projects..."
                  className="form-input pl-10 w-64"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <select
                className="form-select w-48"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
              >
                <option value="">All Statuses</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
                <option value="Overdue">Overdue</option>
                <option value="On Hold">On Hold</option>
              </select>
            </div>
            <div className="text-sm text-gray-600">
              {filteredProjects.length} of {projects.length} projects
            </div>
          </div>
        </div>
      </div>

      {/* Projects Table */}
      <div className="card">
        <div className="card-body p-0">
          {filteredProjects.length === 0 ? (
            <div className="text-center py-12">
              <Calendar className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No projects found</h3>
              <p className="mt-1 text-sm text-gray-500">
                {searchTerm || statusFilter ? 'Try adjusting your search terms or filters.' : 'Get started by adding your first project.'}
              </p>
              {!searchTerm && !statusFilter && (
                <div className="mt-6">
                  <Link to="/projects/new" className="btn btn-primary">
                    <Plus size={20} />
                    Add Project
                  </Link>
                </div>
              )}
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>Project</th>
                    <th>Client</th>
                    <th>Status</th>
                    <th>Due Date</th>
                    <th>Amount</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredProjects.map((project) => (
                    <tr key={project.id}>
                      <td>
                        <div>
                          <div className="text-sm font-medium text-gray-900">{project.name}</div>
                          {project.description && (
                            <div className="text-sm text-gray-500 truncate max-w-xs">
                              {project.description}
                            </div>
                          )}
                        </div>
                      </td>
                      <td>
                        <div className="text-sm text-gray-900">{project.client_name}</div>
                        {project.client_email && (
                          <div className="text-sm text-gray-500">{project.client_email}</div>
                        )}
                      </td>
                      <td>
                        <div className="flex items-center space-x-2">
                          {getStatusBadge(project.status, project.due_date)}
                          <select
                            className="form-select text-xs py-1 px-2"
                            value={project.status}
                            onChange={(e) => handleStatusChange(project.id, e.target.value)}
                          >
                            <option value="In Progress">In Progress</option>
                            <option value="Completed">Completed</option>
                            <option value="On Hold">On Hold</option>
                          </select>
                        </div>
                      </td>
                      <td>
                        {project.due_date ? (
                          <div className="flex items-center text-sm text-gray-600">
                            <Calendar size={14} className="mr-1" />
                            {format(parseISO(project.due_date), 'MMM dd, yyyy')}
                          </div>
                        ) : (
                          <span className="text-sm text-gray-400">—</span>
                        )}
                      </td>
                      <td>
                        {project.total_amount ? (
                          <div className="flex items-center text-sm font-medium text-gray-900">
                            <DollarSign size={14} className="mr-1" />
                            ${parseFloat(project.total_amount).toFixed(2)}
                          </div>
                        ) : (
                          <span className="text-sm text-gray-400">—</span>
                        )}
                      </td>
                      <td>
                        <div className="flex items-center space-x-2">
                          <Link
                            to={`/projects/${project.id}`}
                            className="text-blue-600 hover:text-blue-500"
                            title="View details"
                          >
                            <Eye size={16} />
                          </Link>
                          <Link
                            to={`/projects/${project.id}/edit`}
                            className="text-gray-600 hover:text-gray-500"
                            title="Edit project"
                          >
                            <Edit size={16} />
                          </Link>
                          <button
                            onClick={() => {
                              setProjectToDelete(project);
                              setShowDeleteModal(true);
                            }}
                            className="text-red-600 hover:text-red-500"
                            title="Delete project"
                          >
                            <Trash2 size={16} />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && projectToDelete && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3 text-center">
              <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100">
                <Trash2 className="h-6 w-6 text-red-600" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mt-4">Delete Project</h3>
              <div className="mt-2 px-7 py-3">
                <p className="text-sm text-gray-500">
                  Are you sure you want to delete <strong>{projectToDelete.name}</strong>? 
                  This action cannot be undone.
                </p>
              </div>
              <div className="flex justify-center space-x-4 mt-4">
                <button
                  onClick={() => {
                    setShowDeleteModal(false);
                    setProjectToDelete(null);
                  }}
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleDelete(projectToDelete.id)}
                  className="btn btn-danger"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Projects;
