import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Plus, Search, Edit, Trash2, Eye, Calendar, DollarSign, CreditCard, FileText } from 'lucide-react';
import { format, parseISO } from 'date-fns';

const Invoices = () => {
  const [invoices, setInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [invoiceToDelete, setInvoiceToDelete] = useState(null);

  useEffect(() => {
    fetchInvoices();
  }, []);

  const fetchInvoices = async () => {
    try {
      const response = await axios.get('/api/invoices');
      setInvoices(response.data.invoices);
    } catch (error) {
      console.error('Error fetching invoices:', error);
      toast.error('Failed to fetch invoices');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (invoiceId) => {
    try {
      await axios.delete(`/api/invoices/${invoiceId}`);
      setInvoices(invoices.filter(invoice => invoice.id !== invoiceId));
      toast.success('Invoice deleted successfully');
      setShowDeleteModal(false);
      setInvoiceToDelete(null);
    } catch (error) {
      console.error('Error deleting invoice:', error);
      toast.error(error.response?.data?.message || 'Failed to delete invoice');
    }
  };

  const handleStatusChange = async (invoiceId, newStatus) => {
    try {
      const response = await axios.patch(`/api/invoices/${invoiceId}/status`, {
        status: newStatus
      });
      
      setInvoices(invoices.map(invoice => 
        invoice.id === invoiceId 
          ? { ...invoice, status: newStatus }
          : invoice
      ));
      
      toast.success('Invoice status updated successfully');
    } catch (error) {
      console.error('Error updating invoice status:', error);
      toast.error('Failed to update invoice status');
    }
  };

  const handlePayNow = async (invoiceId) => {
    try {
      const invoice = invoices.find(inv => inv.id === invoiceId);
      if (!invoice) return;

      const response = await axios.post('/api/payments/create-checkout-session', {
        invoiceId: invoiceId,
        amount: parseFloat(invoice.amount)
      });

      // Redirect to Stripe Checkout
      window.location.href = response.data.url;
    } catch (error) {
      console.error('Error creating payment session:', error);
      toast.error('Failed to create payment session');
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      'Draft': { class: 'badge-warning' },
      'Sent': { class: 'badge-info' },
      'Paid': { class: 'badge-success' },
      'Overdue': { class: 'badge-danger' },
    };

    const config = statusConfig[status] || statusConfig['Draft'];

    return (
      <span className={`badge ${config.class}`}>
        {status}
      </span>
    );
  };

  const filteredInvoices = invoices.filter(invoice => {
    const matchesSearch = 
      invoice.invoice_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
      invoice.client_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      invoice.project_name.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesStatus = !statusFilter || invoice.status === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  if (loading) {
    return (
      <div className="loading">
        <div>Loading invoices...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Invoices</h1>
          <p className="mt-1 text-sm text-gray-600">
            Create and manage invoices for your projects.
          </p>
        </div>
        <Link to="/invoices/new" className="btn btn-primary">
          <Plus size={20} />
          Create Invoice
        </Link>
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Search invoices..."
                  className="form-input pl-10 w-64"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <select
                className="form-select w-48"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
              >
                <option value="">All Statuses</option>
                <option value="Draft">Draft</option>
                <option value="Sent">Sent</option>
                <option value="Paid">Paid</option>
                <option value="Overdue">Overdue</option>
              </select>
            </div>
            <div className="text-sm text-gray-600">
              {filteredInvoices.length} of {invoices.length} invoices
            </div>
          </div>
        </div>
      </div>

      {/* Invoices Table */}
      <div className="card">
        <div className="card-body p-0">
          {filteredInvoices.length === 0 ? (
            <div className="text-center py-12">
              <FileText className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No invoices found</h3>
              <p className="mt-1 text-sm text-gray-500">
                {searchTerm || statusFilter ? 'Try adjusting your search terms or filters.' : 'Get started by creating your first invoice.'}
              </p>
              {!searchTerm && !statusFilter && (
                <div className="mt-6">
                  <Link to="/invoices/new" className="btn btn-primary">
                    <Plus size={20} />
                    Create Invoice
                  </Link>
                </div>
              )}
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>Invoice</th>
                    <th>Client</th>
                    <th>Project</th>
                    <th>Amount</th>
                    <th>Status</th>
                    <th>Due Date</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredInvoices.map((invoice) => (
                    <tr key={invoice.id}>
                      <td>
                        <div>
                          <div className="text-sm font-medium text-gray-900">{invoice.invoice_number}</div>
                          <div className="text-sm text-gray-500">
                            {format(parseISO(invoice.created_at), 'MMM dd, yyyy')}
                          </div>
                        </div>
                      </td>
                      <td>
                        <div className="text-sm text-gray-900">{invoice.client_name}</div>
                      </td>
                      <td>
                        <div className="text-sm text-gray-900">{invoice.project_name}</div>
                      </td>
                      <td>
                        <div className="flex items-center text-sm font-medium text-gray-900">
                          <DollarSign size={14} className="mr-1" />
                          ${parseFloat(invoice.amount).toFixed(2)}
                        </div>
                      </td>
                      <td>
                        <div className="flex items-center space-x-2">
                          {getStatusBadge(invoice.status)}
                          {invoice.status !== 'Paid' && (
                            <select
                              className="form-select text-xs py-1 px-2"
                              value={invoice.status}
                              onChange={(e) => handleStatusChange(invoice.id, e.target.value)}
                            >
                              <option value="Draft">Draft</option>
                              <option value="Sent">Sent</option>
                              <option value="Overdue">Overdue</option>
                            </select>
                          )}
                        </div>
                      </td>
                      <td>
                        {invoice.due_date ? (
                          <div className="flex items-center text-sm text-gray-600">
                            <Calendar size={14} className="mr-1" />
                            {format(parseISO(invoice.due_date), 'MMM dd, yyyy')}
                          </div>
                        ) : (
                          <span className="text-sm text-gray-400">â€”</span>
                        )}
                      </td>
                      <td>
                        <div className="flex items-center space-x-2">
                          <Link
                            to={`/invoices/${invoice.id}`}
                            className="text-blue-600 hover:text-blue-500"
                            title="View invoice"
                          >
                            <Eye size={16} />
                          </Link>
                          {invoice.status !== 'Paid' && (
                            <>
                              <Link
                                to={`/invoices/${invoice.id}/edit`}
                                className="text-gray-600 hover:text-gray-500"
                                title="Edit invoice"
                              >
                                <Edit size={16} />
                              </Link>
                              <button
                                onClick={() => handlePayNow(invoice.id)}
                                className="text-green-600 hover:text-green-500"
                                title="Pay now"
                              >
                                <CreditCard size={16} />
                              </button>
                            </>
                          )}
                          {invoice.status !== 'Paid' && (
                            <button
                              onClick={() => {
                                setInvoiceToDelete(invoice);
                                setShowDeleteModal(true);
                              }}
                              className="text-red-600 hover:text-red-500"
                              title="Delete invoice"
                            >
                              <Trash2 size={16} />
                            </button>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && invoiceToDelete && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3 text-center">
              <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100">
                <Trash2 className="h-6 w-6 text-red-600" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mt-4">Delete Invoice</h3>
              <div className="mt-2 px-7 py-3">
                <p className="text-sm text-gray-500">
                  Are you sure you want to delete invoice <strong>{invoiceToDelete.invoice_number}</strong>? 
                  This action cannot be undone.
                </p>
              </div>
              <div className="flex justify-center space-x-4 mt-4">
                <button
                  onClick={() => {
                    setShowDeleteModal(false);
                    setInvoiceToDelete(null);
                  }}
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleDelete(invoiceToDelete.id)}
                  className="btn btn-danger"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Invoices;
